pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * @title MockERC20
 * @notice Mock ERC20 token for testing purposes
 */
contract MockERC20 is ERC20 {
    uint8 private _decimals;

    constructor(
        string memory name,
        string memory symbol,
        uint8 decimals_
    ) ERC20(name, symbol) {
        _decimals = decimals_;
    }

    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    function mint(address to, uint256 amount) external {
        _mint(to, amount);
    }

    function burn(address from, uint256 amount) external {
        _burn(from, amount);
    }
}

contracts/StablecoinSwitch.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title StablecoinSwitch
 * @notice Cross-chain stablecoin settlement switch with LI.FI integration and Chainlink fallback
 * @dev MVP contract for routing cross-chain stablecoin transfers with priority-based routing
 *
 * EXTENSION GUIDE:
 * 1. Bridge Adapters: Create contracts implementing IBridgeAdapter interface
 * 2. LI.FI Integration: Use ILiFi interface to call their router contract
 * 3. Chainlink CCIP: Implement IRouterClient for production fallback
 * 4. Add real price feeds via Chainlink oracles for dynamic cost calculation
 */

interface IBridgeAdapter {
    function bridge(
        address token,
        uint256 amount,
        uint256 toChainId,
        address recipient
    ) external returns (bool success);

    function estimateFee(
        address token,
        uint256 amount,
        uint256 toChainId
    ) external view returns (uint256 fee);
}

interface ILiFi {
    struct BridgeData {
        bytes32 transactionId;
        string bridge;
        string integrator;
        address referrer;
        address sendingAssetId;
        address receiver;
        uint256 minAmount;
        uint256 destinationChainId;
        bool hasSourceSwaps;
        bool hasDestinationCall;
    }

    function startBridgeTokensViaGenericBridge(
        BridgeData calldata _bridgeData,
        bytes calldata _genericData
    ) external payable;
}

interface IChainlinkRouter {
    struct EVM2AnyMessage {
        bytes receiver;
        bytes data;
        address[] tokenAmounts;
        address feeToken;
        bytes extraArgs;
    }

    function ccipSend(
        uint64 destinationChainSelector,
        EVM2AnyMessage memory message
    ) external payable returns (bytes32 messageId);
}

contract StablecoinSwitch is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    // Route priority types
    enum Priority {
        Cost,
        Speed
    }

    // Optimal path structure
    struct OptimalPath {
        uint256 fromChainId;
        uint256 toChainId;
        uint256 cost; // in basis points (100 = 1%)
        uint256 speed; // estimated time in seconds
        address bridge; // bridge adapter address
        string bridgeName; // "LiFi" or "Chainlink"
    }

    // Bridge adapters
    address public liFiBridgeAdapter;
    address public chainlinkBridgeAdapter;
    address public customBridgeAdapter;

    // LI.FI and Chainlink router addresses
    address public liFiRouter;
    address public chainlinkRouter;

    // Chain ID configuration
    uint256 public immutable currentChainId;

    // Mock route data (for demo purposes)
    mapping(uint256 => RouteInfo) public mockRoutes;

    struct RouteInfo {
        uint256 costBps; // Cost in basis points
        uint256 speedSeconds; // Speed in seconds
        bool active;
    }

    // Events
    event TransactionRouted(
        address indexed user,
        address indexed fromToken,
        uint256 amount,
        uint256 toChainId,
        Priority priority,
        OptimalPath route,
        uint256 timestamp
    );

    event SettlementExecuted(
        address indexed token,
        uint256 amount,
        address indexed recipient,
        uint256 toChainId,
        string bridge,
        uint256 timestamp
    );

    event BridgeAdapterSet(
        string bridgeType,
        address indexed adapter,
        uint256 timestamp
    );

    event LiFiBridgeFailed(
        address indexed token,
        uint256 amount,
        address indexed recipient,
        string reason
    );

    event FallbackToChainlink(
        address indexed token,
        uint256 amount,
        address indexed recipient,
        uint256 toChainId
    );

    // Errors
    error InvalidAmount();
    error InvalidChainId();
    error InvalidToken();
    error InvalidPriority();
    error InvalidRecipient();
    error NoRouteAvailable();
    error InsufficientBalance();
    error TransferFailed();
    error BridgeNotConfigured();
    error BridgeFailed(string reason);

    constructor() {
        currentChainId = block.chainid;

        // Initialize mock routes for demo (Arbitrum One = 42161)
        // Route to Ethereum mainnet
        mockRoutes[1] = RouteInfo({
            costBps: 30, // 0.3% fee
            speedSeconds: 600, // 10 minutes
            active: true
        });

        // Route to Polygon
        mockRoutes[137] = RouteInfo({
            costBps: 20, // 0.2% fee
            speedSeconds: 300, // 5 minutes
            active: true
        });

        // Route to Optimism
        mockRoutes[10] = RouteInfo({
            costBps: 25, // 0.25% fee
            speedSeconds: 400, // 6-7 minutes
            active: true
        });
    }

    /**
     * @notice Route a cross-chain stablecoin transaction
     * @param fromToken Token address to transfer
     * @param amount Amount to transfer
     * @param toChainId Destination chain ID
     * @param priority 0 for Cost-first, 1 for Speed-first
     */
    function routeTransaction(
        address fromToken,
        uint256 amount,
        uint256 toChainId,
        uint8 priority
    ) external nonReentrant {
        if (amount == 0) revert InvalidAmount();
        if (fromToken == address(0)) revert InvalidToken();
        if (toChainId == currentChainId) revert InvalidChainId();
        if (priority > 1) revert InvalidPriority();

        // Get optimal path based on priority
        OptimalPath memory path = getOptimalPath(
            fromToken,
            amount,
            toChainId,
            Priority(priority)
        );

        if (path.bridge == address(0)) revert NoRouteAvailable();

        // Transfer tokens from user to contract
        IERC20(fromToken).safeTransferFrom(msg.sender, address(this), amount);

        // Emit routing event
        emit TransactionRouted(
            msg.sender,
            fromToken,
            amount,
            toChainId,
            Priority(priority),
            path,
            block.timestamp
        );

        // Execute settlement with LI.FI primary, Chainlink fallback
        _executeSettlementWithFallback(
            fromToken,
            amount,
            msg.sender,
            toChainId
        );
    }

    /**
     * @notice Get optimal path for a transaction
     * @param token Token address
     * @param amount Transfer amount
     * @param toChainId Destination chain ID
     * @param priority Routing priority
     * @return OptimalPath struct with route details
     */
    function getOptimalPath(
        address token,
        uint256 amount,
        uint256 toChainId,
        Priority priority
    ) public view returns (OptimalPath memory) {
        RouteInfo memory route = mockRoutes[toChainId];

        if (!route.active) {
            return
                OptimalPath({
                    fromChainId: 0,
                    toChainId: 0,
                    cost: 0,
                    speed: 0,
                    bridge: address(0),
                    bridgeName: ""
                });
        }

        // Determine bridge based on priority and availability
        address selectedBridge;
        string memory bridgeName;

        if (priority == Priority.Speed) {
            // Speed-first: prefer LI.FI
            if (liFiBridgeAdapter != address(0)) {
                selectedBridge = liFiBridgeAdapter;
                bridgeName = "LiFi";
            } else if (chainlinkBridgeAdapter != address(0)) {
                selectedBridge = chainlinkBridgeAdapter;
                bridgeName = "Chainlink";
            } else {
                selectedBridge = customBridgeAdapter;
                bridgeName = "Custom";
            }
        } else {
            // Cost-first: calculate best option
            // In production, query actual fees from bridges
            if (chainlinkBridgeAdapter != address(0)) {
                selectedBridge = chainlinkBridgeAdapter;
                bridgeName = "Chainlink";
            } else if (liFiBridgeAdapter != address(0)) {
                selectedBridge = liFiBridgeAdapter;
                bridgeName = "LiFi";
            } else {
                selectedBridge = customBridgeAdapter;
                bridgeName = "Custom";
            }
        }

        uint256 calculatedCost = (amount * route.costBps) / 10000;

        return
            OptimalPath({
                fromChainId: currentChainId,
                toChainId: toChainId,
                cost: calculatedCost,
                speed: route.speedSeconds,
                bridge: selectedBridge,
                bridgeName: bridgeName
            });
    }

    /**
     * @notice Execute settlement with LI.FI primary and Chainlink fallback
     * @param token Token address
     * @param amount Amount to bridge
     * @param recipient Recipient address
     * @param toChainId Destination chain ID
     */
    function _executeSettlementWithFallback(
        address token,
        uint256 amount,
        address recipient,
        uint256 toChainId
    ) internal {
        bool lifiSuccess = false;

        // Try LI.FI first
        if (liFiBridgeAdapter != address(0)) {
            try this._executeLiFiBridge(token, amount, recipient, toChainId) {
                lifiSuccess = true;
                emit SettlementExecuted(
                    token,
                    amount,
                    recipient,
                    toChainId,
                    "LiFi",
                    block.timestamp
                );
            } catch Error(string memory reason) {
                emit LiFiBridgeFailed(token, amount, recipient, reason);
            } catch {
                emit LiFiBridgeFailed(
                    token,
                    amount,
                    recipient,
                    "Unknown error"
                );
            }
        }

        // Fallback to Chainlink if LI.FI failed
        if (!lifiSuccess) {
            if (chainlinkBridgeAdapter == address(0))
                revert BridgeNotConfigured();

            emit FallbackToChainlink(token, amount, recipient, toChainId);
            _executeChainlinkBridge(token, amount, recipient, toChainId);

            emit SettlementExecuted(
                token,
                amount,
                recipient,
                toChainId,
                "Chainlink",
                block.timestamp
            );
        }
    }

    /**
     * @notice Execute LI.FI bridge (external call for try-catch)
     * @param token Token address
     * @param amount Amount to bridge
     * @param recipient Recipient address
     * @param toChainId Destination chain ID
     */
    function _executeLiFiBridge(
        address token,
        uint256 amount,
        address recipient,
        uint256 toChainId
    ) external {
        require(msg.sender == address(this), "Internal only");

        if (liFiRouter == address(0)) revert BridgeNotConfigured();

        // Approve LI.FI router to spend tokens
        IERC20(token).safeApprove(liFiRouter, amount);

        // Prepare LI.FI bridge data
        ILiFi.BridgeData memory bridgeData = ILiFi.BridgeData({
            transactionId: keccak256(
                abi.encodePacked(recipient, amount, block.timestamp)
            ),
            bridge: "stargate", // Example bridge, configure as needed
            integrator: "StablecoinSwitch",
            referrer: address(0),
            sendingAssetId: token,
            receiver: recipient,
            minAmount: (amount * 99) / 100, // 1% slippage tolerance
            destinationChainId: toChainId,
            hasSourceSwaps: false,
            hasDestinationCall: false
        });

        // Call LI.FI router
        ILiFi(liFiRouter).startBridgeTokensViaGenericBridge(bridgeData, "");
    }

    /**
     * @notice Execute Chainlink CCIP bridge as fallback
     * @param token Token address
     * @param amount Amount to bridge
     * @param recipient Recipient address
     * @param toChainId Destination chain selector
     */
    function _executeChainlinkBridge(
        address token,
        uint256 amount,
        address recipient,
        uint256 toChainId
    ) internal {
        if (chainlinkRouter == address(0)) revert BridgeNotConfigured();

        // Approve Chainlink router
        IERC20(token).safeApprove(chainlinkRouter, amount);

        // Note: In production, implement proper Chainlink CCIP message construction
        // This is a simplified placeholder
        address[] memory tokens = new address[](1);
        tokens[0] = token;

        IChainlinkRouter.EVM2AnyMessage memory message = IChainlinkRouter
            .EVM2AnyMessage({
                receiver: abi.encode(recipient),
                data: "",
                tokenAmounts: tokens,
                feeToken: address(0),
                extraArgs: ""
            });

        // Cast toChainId to uint64 for Chainlink selector
        IChainlinkRouter(chainlinkRouter).ccipSend(uint64(toChainId), message);
    }

    /**
     * @notice Execute manual settlement (for custom bridges or testing)
     * @param token Token address
     * @param amount Amount to settle
     * @param recipient Recipient address
     * @param toChainId Destination chain ID
     */
    function executeSettlement(
        address token,
        uint256 amount,
        address recipient,
        uint256 toChainId
    ) external onlyOwner nonReentrant {
        if (amount == 0) revert InvalidAmount();
        if (token == address(0)) revert InvalidToken();
        if (recipient == address(0)) revert InvalidRecipient();
        if (toChainId == currentChainId) revert InvalidChainId();

        uint256 balance = IERC20(token).balanceOf(address(this));
        if (balance < amount) revert InsufficientBalance();

        // Use custom bridge adapter if configured
        if (customBridgeAdapter != address(0)) {
            IERC20(token).safeApprove(customBridgeAdapter, amount);
            bool success = IBridgeAdapter(customBridgeAdapter).bridge(
                token,
                amount,
                toChainId,
                recipient
            );
            if (!success) revert BridgeFailed("Custom bridge failed");
        }

        emit SettlementExecuted(
            token,
            amount,
            recipient,
            toChainId,
            "Custom",
            block.timestamp
        );
    }

    /**
     * @notice Set LI.FI bridge adapter address
     * @param adapter LI.FI adapter contract address
     */
    function setLiFiBridgeAdapter(address adapter) external onlyOwner {
        liFiBridgeAdapter = adapter;
        emit BridgeAdapterSet("LiFi", adapter, block.timestamp);
    }

    /**
     * @notice Set Chainlink bridge adapter address
     * @param adapter Chainlink adapter contract address
     */
    function setChainlinkBridgeAdapter(address adapter) external onlyOwner {
        chainlinkBridgeAdapter = adapter;
        emit BridgeAdapterSet("Chainlink", adapter, block.timestamp);
    }

    /**
     * @notice Set custom bridge adapter address
     * @param adapter Custom adapter contract address
     */
    function setBridgeAdapter(address adapter) external onlyOwner {
        customBridgeAdapter = adapter;
        emit BridgeAdapterSet("Custom", adapter, block.timestamp);
    }

    /**
     * @notice Set LI.FI router address
     * @param router LI.FI router contract address
     */
    function setLiFiRouter(address router) external onlyOwner {
        liFiRouter = router;
    }

    /**
     * @notice Set Chainlink router address
     * @param router Chainlink CCIP router address
     */
    function setChainlinkRouter(address router) external onlyOwner {
        chainlinkRouter = router;
    }

    /**
     * @notice Update mock route data for demo purposes
     * @param chainId Target chain ID
     * @param costBps Cost in basis points
     * @param speedSeconds Speed in seconds
     * @param active Whether route is active
     */
    function updateMockRoute(
        uint256 chainId,
        uint256 costBps,
        uint256 speedSeconds,
        bool active
    ) external onlyOwner {
        mockRoutes[chainId] = RouteInfo({
            costBps: costBps,
            speedSeconds: speedSeconds,
            active: active
        });
    }

    /**
     * @notice Emergency token withdrawal
     * @param token Token address to withdraw
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(
        address token,
        uint256 amount
    ) external onlyOwner {
        IERC20(token).safeTransfer(owner(), amount);
    }

    /**
     * @notice Get current chain ID
     */
    function getCurrentChainId() external view returns (uint256) {
        return currentChainId;
    }
}

