            vm.assertTrue(data, err);
        }
    }

    function assertFalse(bool data) internal pure virtual {
        if (data) {
            vm.assertFalse(data);
        }
    }

    function assertFalse(bool data, string memory err) internal pure virtual {
        if (data) {
